import React from "react";
import { withRouter } from "react-router";
import axios from "axios";

class PokemonDetail extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      abilities: [],
      dict : []
    }
    this.handlePokemonListResponse = this.handlePokemonListResponse.bind(this);
    this.handlePokemonDetailsResponse = this.handlePokemonDetailsResponse.bind(this)
    this.mapPokemonToListItem = this.mapPokemonToListItem.bind(this)

  }



    render() {
      const pokemon = this.props.pokemons.find(
        (pokemon) => pokemon.id === parseInt(this.props.match.params.id, 10)
      );
      const pokemonAbility = pokemon.abilities;


    var dict = [];
    console.log("state abilities len: " + pokemonAbility.length)
    for (let i = 0; i < pokemonAbility.length; i++){
      dict.push({
        key:   pokemonAbility[i],
        value: this.state.abilities[i]
    });
  }
  this.setState({dict : dict});
  console.log("dic hossza: " + dict.length)

    return (
      <React.Fragment>
        <h1>Pokemon Details:</h1>
        <img alt="pokemonDetails" src={pokemon.image_url} />

        <h4>Pokemon name:</h4>
        {pokemon.name}

        <h4>Abilities:</h4>
        {pokemonAbility.map(this.mapPokemonToListItem)}


        { console.log("hosszusag: " + this.state.dict.length)}
        { console.log("teszt value: " + this.state.dict[0].id)}
        {
          Object.keys(dict).map((key, index) => ( 
            <p key={index}> this is my key {key} and this is my value:  {this.state.dict[{key}]} ! </p> 
          ))
        }


      </React.Fragment>
    );
  }

  componentDidMount(){
    const pokemon = this.props.pokemons.find(
      (pokemon) => pokemon.id === parseInt(this.props.match.params.id, 10)
    );
    const pokemonAbility = pokemon.abilities;

    this.handlePokemonListResponse(pokemonAbility);
  }


  handlePokemonListResponse(pokemonAbility) {
    console.log("length: " , pokemonAbility)
    Promise.all(pokemonAbility.map((element) => axios.get(element.ability.url)))
      .then(this.handlePokemonDetailsResponse)
  }

  toAbility(response) {
    return response.data.effect_entries[1].effect
  }

  handlePokemonDetailsResponse(responses) {
    console.log("responses: ", responses);
    this.setState({
      abilities: responses.map(this.toAbility)
    });
  }

  mapPokemonToListItem(pokemon) {
  return <p key={pokemon.abilities}> {pokemon.ability.name} -  </p>;
  }



}

export default withRouter(PokemonDetail);
