import React from 'react';
import { withRouter } from "react-router";
import axios from 'axios';

class PokemonDetail extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loaded: false,
      offset: 0,
      pokemons: []
    }

    this.handlePokemonListResponse = this.handlePokemonListResponse.bind(this);
    this.handlePokemonDetailsResponse = this.handlePokemonDetailsResponse.bind(this);
    this.handleGetPrev20 = this.handleGetPrev20.bind(this);
    this.handleGetNext20 = this.handleGetNext20.bind(this);
    this.fetchPokemons = this.fetchPokemons.bind(this);
  }

  render() {
    const pokemon = this.props.pokemons.find((pokemon) => pokemon.id === parseInt(this.props.match.params.id, 10))
    const pokemonAbility = pokemon.abilities;


    return (
      <React.Fragment>
        <h1>{pokemon.name}</h1>

        <img alt="pokemonDetails" src={pokemon.image_url}/>

        {pokemonAbility.map(this.mapPokemonToListItem)}
      </React.Fragment>
    );  
  }

  componentDidMount() {
    const pokemon = this.props.pokemons.find((pokemon) => pokemon.id === parseInt(this.props.match.params.id, 10))
    axios.get(pokemon.ability.url.toString()).then((response) => this.setState({types: response.data.results}))
  }

  mapPokemonToListItem(pokemon) {
    return (
      <p key={pokemon.abilities}>
          {pokemon.ability.name}
          {pokemon.ability.url}
      </p>
    )
  }


}

export default withRouter(PokemonDetail);
